import React, { Component, Fragment } from 'react';
import Particles from 'react-particles-js';
import Footer from './Components/Footer/Footer'
import Nav from './Components/Nav/Nav';
import Home from './Components/Pages/Home'
import Events from './Components/Pages/Events'
import Sponsors from './Components/Pages/Sponsors'
import {BrowserRouter as Router, NavLink} from 'react-router-dom';
import Route from 'react-router-dom/Route';

import './App.css';

const particleOptions = {
    "particles": {
        "number": {
            "value": 200,
            density:{
                enable:true,
                value_area:800
            }
        },
        "size": {
            "value": 3
        }
    },
    "interactivity": {
        "events": {
            "onhover": {
                "enable": true,
                "mode": "repulse"
            }
        }
    }
}

class App extends Component {
    state = {
        showComponent: false
    };
    handleClick = () => {
        this.setState({
        showComponent: true
        });
    };
    render(){
        if (this.state.showComponent) {
            return(
                <div className='body'>
                    <Particles className = 'particles'params={particleOptions}/>
                    <Nav />
                    <Router>
                        <Route path="/" exact render = {
                            ()=>{
                                return (<Home />)
                            }
                        }/>
                    </Router>
                    <Router>
                        <Route path="/events" exact render = {
                            ()=>{
                                return (<Events/>)
                            }
                        }/>
                    </Router>
                    <Router>
                        <Route path="/committee" exact render = {
                            ()=>{
                                return (<h1>Committee members and organizers here</h1>)
                            }
                        }/>
                    </Router>
                    <Router>
                        <Route path="/sponsors" exact render = {
                            ()=>{
                                return (<Sponsors/>)
                            }
                        }/>
                    </Router>
                    <Router>
                        <Route path="/contact" exact render = {
                            ()=>{
                                return (<h1>Contacts will be loaded here</h1>)
                            }
                        }/>
                    </Router>
                    <Footer/>

                </div>
            );
        } else {return (
          <Fragment>
            <h1 onLoad={this.handleClick}>Hello!</h1>
          </Fragment>
        );
      }
    }
}

export default App;
